import React from 'react';
import { Button, Collapse } from 'reactstrap';
import { PanelGroup, Panel } from 'react-bootstrap';
import SpaceNavbar from './SpaceNavbar';
import './SpaceAdmin.css';

const candNT = ["hello"];
const candEA = [];
const candT = [];

// Le composant qui affiche la page de l'espace administrateur
class SpaceAdmin extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
          candNonTraite: false,
          candAttente:false,
          candTraite:false
        };
        this.toggleNonTraite = this.toggleNonTraite.bind(this);
        this.toggleAttente = this.toggleAttente.bind(this);
        this.toggleTraite = this.toggleTraite.bind(this);
        this.renderNonTraite = this.renderNonTraite.bind(this);
        this.renderEnAttente = this.renderEnAttente.bind(this);
        this.renderTraite = this.renderTraite.bind(this);
    }
    // Fonction pour afficher le div qui contient les candidatures non traitées
    toggleNonTraite() {
        this.setState({
          candNonTraite: !this.state.candNonTraite,
          candAttente:false,
          candTraite:false
        });
    }
    // Fonction pour afficher le div qui contient les candidatures en attentes
    toggleAttente() {
        this.setState({
          candAttente: !this.state.candAttente,
          candNonTraite:false,
          candTraite:false
        });
    }
    // Fonction pour afficher le div qui contient les candidatures traitées
    toggleTraite() {
        this.setState({
          candTraite: !this.state.candTraite,
          candNonTraite:false,
          candAttente:false
        });
    }
    // Fonction pour afficher les candidatures non traitées
    renderNonTraite() {
        if(candNT.length === 0) {
            return <div className="h" style={{backgroundColor: "silver"}}>IL N'Y A PAS DE CANDIDATURES NON TRAITEES :'(</div>;
        } else {
            return (
                <PanelGroup accordion>
                    <Panel eventKey="1"></Panel>
                </PanelGroup>
            );
        }
    }
    // Fonction pour afficher les candidatures en attentes
    renderEnAttente() {
        if(candEA.length === 0) {
            return <div className="h" style={{backgroundColor: "gray"}}>IL N'Y A PAS DE CANDIDATURES EN ATTENTES :'(</div>;
        } else {
            return <div className="h" style={{backgroundColor: "gray"}}>LISTE DES CANDIDATURES EN ATTENTES :</div>;
        }
    }
    // Fonction pour afficher les candidatures traitées
    renderTraite() {
        if(candT.length === 0) {
            return <div className="h" style={{backgroundColor: "black"}}>IL N'Y A PAS DE CANDIDATURES TRAITEES :'(</div>;
        } else {
            return <div className="h" style={{backgroundColor: "black"}}>LISTE DES CANDIDATURES TRAITEES :</div>;
        }
    }
    render() {
        return(
            <div>
                <SpaceNavbar></SpaceNavbar>
                <div className="text-center">
                    <Button onClick={this.toggleNonTraite}>Afficher les candidatures non traitées</Button>
                    <Button onClick={this.toggleAttente}>Afficher les candidatures en attentes</Button>
                    <Button onClick={this.toggleTraite}>Afficher les candidatures traitées</Button>
                </div>    
                <Collapse isOpen={this.state.candNonTraite}>
                    {this.renderNonTraite()}
                </Collapse>
                <Collapse isOpen={this.state.candAttente}>
                    {this.renderEnAttente()}
                </Collapse>
                <Collapse isOpen={this.state.candTraite}>
                    {this.renderTraite()}
                </Collapse>
            </div>
        );
    }
}

export default SpaceAdmin;
